$white: #fff;
$black: #2e2e2e;
$gray: #787878;

* {
  font-family: "Poppins", sans-serif;
  color: #212121;
  font-size: 1em;
}

body {
  overflow: hidden;
}

.container {
  display: flex;
  flex-direction: column;
  width: 100vw;
  align-items: center;
  justify-content: center;

  .home {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    width: 80%;
    height: 100vh;
    align-items: center;
    justify-content: space-between;

    .left {
      display: flex;
      flex-direction: column;
      flex-basis: 45%;
      align-items: center;
      justify-content: center;

      .titles {
        margin-top: 5%;
        text-align: left;

        h1 {
          font-size: 4em;
          margin-bottom: -5px;
        }

        h4 {
          font-size: 1em;
          font-weight: 400;
          color: lightslategrey;
        }

        p {
          font-size: 1em;
          font-weight: 400;
          color: lightslategrey;
          line-height: 140%;
        }
      }

      .home-buttons {
        display: flex;
        flex-direction: row;
        flex-wrap: wrap;
        width: 100%;
        justify-content: space-between;
        align-items: center;

        .about-button {
          margin-top: 50px;
          margin-right: 40px;
          font-weight: 400;
          color: rgba(255, 171, 157, 0.7);
          text-decoration: underline;
          transition: all 0.4s ease;
          text-align: right;

          &:hover {
            cursor: pointer;
            color: rgba(255, 171, 157, 1);
            transition: all 0.4s ease;
          }
        }

        .reserve-button {
          position: relative;
          margin-top: 40px;
          padding: 19px 22px;
          transition: all 0.2s ease;

          &:before {
            content: "";
            position: absolute;
            top: 0;
            left: 0;
            display: block;
            border-radius: 28px;
            background: rgba(255, 171, 157, 0.5);
            width: 56px;
            height: 56px;
            transition: all 0.3s ease;
          }
          & span {
            position: relative;
            font-size: 15px;
            line-height: 18px;
            font-weight: 900;
            letter-spacing: 0.25em;
            text-transform: uppercase;
            vertical-align: middle;
          }
          & svg {
            position: relative;
            top: 0;
            margin-left: 10px;
            fill: none;
            stroke-linecap: round;
            stroke-linejoin: round;
            stroke: #111;
            stroke-width: 2;
            transform: translateX(-5px);
            transition: all 0.3s ease;
          }
          &:hover:before {
            width: 100%;
            background: #ffab9d;
          }
          &:hover svg {
            transform: translateX(0);
          }
          &:active {
            transform: scale(0.96);
          }
        }
      }
    }

    .right {
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      flex-basis: 45%;
      min-width: 600px;
      height: 100%;

      .img-container {
        max-height: 400px;
        cursor: pointer;
        overflow: hidden;
        &:hover .img-content-hover {
          display: block;
        }
      }

      img {
        margin: auto;
        width: 100%;
        max-width: 600px;
        height: 500px;
        object-fit: cover;
        transform: scale(1);
        transition: all 0.3s ease-in-out;
        &:hover {
          transform: scale(1.05);
        }
      }

      .img-content-hover {
        position: absolute;
        top: 0;
        left: 0;
        white-space: nowrap;
        display: none;
        padding: 1rem;
        background: $white;
        font-weight: 400;
        margin-top: 1.25rem;
        margin-left: -2rem;
      }

      .title {
        color: $black;
        font-size: 1.5rem;
        font-weight: 700;
      }

      .category {
        font-size: 1rem;
        color: $gray;
      }

      .img-content {
        display: none;
      }
    }

    .home-intro {
      display: flex;
      flex-direction: row;
      width: 70%;
      justify-content: space-around;

      p {
        margin: 20px;
      }
    }
  }

  // About
  .popups-container {
    position: absolute;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    width: 100vw;
    height: 100vh;

    .film-popup {
      z-index: 9;
      display: none;
      opacity: 0;
      position: absolute;
      top: 0px;
      left: 0px;
      width: 100vw;
      height: 100vh;
      background: rgba(0, 0, 0, 0.7);
      backdrop-filter: blur(30px);

      flex-direction: row;
      justify-content: center;
      align-items: center;

      transition: 1s;

      &.active {
        display: flex;
        transition: 0.4s;
      }

      .film-popup-content {
        position: relative;
        width: 50%;
        padding: 2%;
        border-radius: 25px;
        background-color: white;
        display: flex;
        flex-direction: row;
        justify-content: center;
        align-items: center;

        transform: translateY(50px);
        opacity: 1;
        transition: transform 0.2s;

        &.active {
          transform: translateY(0px);
          opacity: 1;
          transition: transform 0.2s;
        }

        .close-pupups {
          position: absolute;
          top: 30px;
          right: 50px;
          font-size: 1.1em;
          font-weight: 400;
          color: rgba(255, 171, 157);
          transition: all 0.4s ease;
          text-align: right;

          &:hover {
            cursor: pointer;
            color: rgba(255, 171, 157, 1);
            transition: all 0.4s ease;
          }
        }

        .right {
          flex-basis: 40%;
        }
        .left {
          flex-basis: 50%;
        }
      }
    }
  }

  .content-popup {
    transform: translateY(120%);
    transition: all 0.3s ease-in;
    position: absolute;
    bottom: 0px;
    left: 0px;
    width: 100vw;
    height: 95vh;
    background-color: white;
    border-top: 1px solid lightgray;
    border-top-right-radius: 40px;
    border-top-left-radius: 40px;
    box-shadow: 0px -5px 25px grey;

    &.active {
      transform: translateY(0);
      transition: transform 0.3s ease-in;
    }

    .close-about-popup {
      outline: none;
      position: absolute;
      top: 30px;
      left: 30px;
      border: none;
      border-radius: 15px;
      padding: 10px 30px;

      background-color: rgba(255, 171, 157);
      color: white;
      transition: 0.4s;

      .arrow-icon {
        color: white;
      }

      &:hover {
        cursor: pointer;
        padding: 12px 32px;
        transform: translateY(-2px);
        transition: 0.4s;
      }
    }

    .about {
      width: 80%;
      height: 100%;
      margin: auto;
      display: flex;
      flex-direction: row;
      justify-content: space-between;
      align-items: center;

      .left {
        flex-basis: 45%;

        .filmslist {
          display: flex;
          flex-direction: row;
          flex-wrap: wrap;
          justify-content: center;
          align-items: center;

          .film {
            width: 120px;
            margin: 10px;

            &:hover {
              cursor: pointer;
            }

            .film-image {
              width: 120px;
            }
          }
        }
      }

      .right {
        flex-basis: 45%;
        height: 100%;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: flex-start;

        .about-festival {
          font-size: 3em;
          margin-bottom: 20px;
        }
      }
    }
  }
}

